mapper.select:
select

from sys_service_config

     and seq_id = #{seqId,jdbcType=BIGINT} 
     and application_code = #{applicationCode,jdbcType=VARCHAR} 
     and service_type = #{serviceType,jdbcType=INTEGER} 
     and component_name = #{componentName,jdbcType=VARCHAR} 
     and state = #{state,jdbcType=INTEGER}
